import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {
  render() {

    const {name,done} = this.props;
    return (
      <li onMouseOver={this.handleMouseOver} onMouseLeave={this.handleMouse}>
        <label>
          <input type="checkbox" defaultChecked={done}/>
          <span>{name}</span>
        </label>
        <button ref={this.getButton} className="btn btn-danger" style={{ display: 'none' }}>删除</button>
      </li>
    )
  }

  handleMouseOver = (event)=>{
      const {target} = event;
      target.style.backgroundColor = 'grey';
      this.btn.style.display = 'block';
  }

  getButton = (c)=>{
    this.btn = c;
  }


}
